<span class="line"><span class="line"><span style="color:#81A1C1">int</span><span style="color:#88C0D0"> pthread_create</span><span style="color:#ECEFF4">(</span><span style="color:#81A1C1">pthread_t</span><span style="color:#81A1C1"> *</span><span style="color:#D8DEE9">thread</span><span style="color:#ECEFF4">,</span><span style="color:#616E88"> // Thread Identifier</span></span></span>
<span class="line"><span class="line"><span style="color:#81A1C1">                   const</span><span style="color:#81A1C1"> pthread_attr_t</span><span style="color:#81A1C1"> *</span><span style="color:#D8DEE9">attr</span><span style="color:#ECEFF4">,</span><span style="color:#616E88"> // The thread's own attributes</span></span></span>
